import { StyleSheet, Text, View } from 'react-native';
import React, { useState, useEffect } from 'react';
import ShadowedBox from 'components/ShadowedBox';
import { ScrollView } from 'react-native-gesture-handler';
import { globalRunner } from 'model/Runner';
import Job from 'model/Job';

export default function RunnerHistoryScreen({ navigation }) {

	const [taskList, setTaskList] = useState([]);
	const [runner, setRunner] = useState("");

	useEffect(() => {
		setRunner(globalRunner.key);
		setTaskList(Job.getRunnerJobs(globalRunner.id));
	}, [])

	const textColor = (text) => {
		if (text == 'Complete') {
			return '#51A760'
		} else if (text == 'In transit') {
			return '#E9C041'
		} else if (text == 'Unstarted') {
			return '#CD594A'
		}
		return '#0070F7'
	}

	const taskHistory = taskList.map(task => {
		return (
			<View style={styles.rowView}>
				<View style={{
					flexDirection: 'column',
					flex: 7,
				}}>
					<Text style={styles.rowTitle}>{task.item}</Text>
					<Text style={styles.rowText}>{task.from} to {task.to}</Text>
				</View>
				<Text style={{
					...styles.rowTitle, 
					color: textColor(task.status),
					textAlign: "right",
					flex: 3,
					}}>{task.status}</Text>
			</View>
		);
	});

	return (
		<View style={styles.container}>
			<ShadowedBox width={'90%'} margin={10}>
				<View style={{width: '90%', marginTop: 25, marginBottom: 10}}>
					<Text style={styles.sectionTitle}>Runner {runner}: Task History</Text>
				</View>
				<View style = {{width: '90%', marginBottom: 25, maxHeight: '85%'}}>
                    <ScrollView>
                        {taskHistory}
                    </ScrollView>
                </View>
			</ShadowedBox>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#f2f2f2',
		alignItems: 'center',
		justifyContent: 'flex-start',
	},
	rowView: {
        width: "100%",
        flexDirection: "row",
		justifyContent: "space-between",
		alignItems: "flex-end",
		borderBottomColor: "grey",
        borderBottomWidth: StyleSheet.hairlineWidth,
		padding: 5,
		flex: 1,
	},
	sectionTitle: {
        color: "black",
        fontFamily: "Arial-BoldMT",
        fontSize: 20,
        fontWeight: "bold",
        textAlign: "left",
	},
	rowTitle: {
        color: "#5C5A5A",
        fontFamily: "Arial-BoldMT",
        fontSize: 16,
        fontWeight: "bold",
		textAlign: "left",
		marginBottom: 2,
		flexWrap: "wrap",
		width: "100%"
	},
	rowText: {
        color: "#5C5A5A",
        fontFamily: "Arial",
		fontSize: 16,
		fontWeight: "normal",
		textAlign: "left",
		flexWrap: "wrap",
		width: "100%"
    }
});
